scripts.apply:
try {
    bcdedit /set disabledynamictick yes
    Write-Host "Dynamic Ticking disabled successfully"
  } catch {
    Write-Host "Failed to apply Dynamic Ticking tweak"
  }

scripts.unapply:
try {
    bcdedit /set disabledynamictick no
    Write-Host "Dynamic Ticking settings restored"
  } catch {
    Write-Host "Failed to revert Dynamic Ticking tweak"
  }

scripts.apply:
try {
    winget uninstall 9nzkpstsnw4p --silent --accept-source-agreements 
        Get-AppxPackage Microsoft.XboxGamingOverlay | Remove-AppxPackage -ErrorAction Stop

        Write-Output 'Successfully removed Xbox Gaming Overlay via AppxPackage'
    }
    catch {
        Write-Output 'AppxPackage removal failed, trying to remove via winget'
        try {
            winget uninstall 9nzkpstsnw4p --silent --accept-source-agreements 
            Write-Output 'Successfully removed Xbox Gaming Overlay via winget'
        }
        catch {
            Write-Output 'Winget uninstall failed'
        }
    }

$regPath = "HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced\TaskbarDeveloperSettings"
$valueName = "TaskbarEndTask"

if (-not (Test-Path $regPath)) {
    New-Item -Path $regPath -Force | Out-Null
}

Set-ItemProperty -Path $regPath -Name $valueName -Type DWord -Value 1

scripts.unapply:
$regPath = "HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced\TaskbarDeveloperSettings"
$valueName = "TaskbarEndTask"

if (-not (Test-Path $regPath)) {
    New-Item -Path $regPath -Force | Out-Null
}

Set-ItemProperty -Path $regPath -Name $valueName -Type DWord -Value 0

$regPath = "HKLM:\System\CurrentControlSet\Control\GraphicsDrivers"
$keyName = "HwSchMode"
$value = 1

If (Test-Path $regPath) {
    Set-ItemProperty -Path $regPath -Name $keyName -Value $value -Type DWord
    Write-Host "Hardware-Accelerated GPU Scheduling disabled. A restart may be required."
}
else {
    Write-Host "Registry path not found."
}

$regPath = "HKCU:\Software\Microsoft\DirectX\UserGpuPreferences"
$keyName = "DirectXUserGlobalSettings"

If (Test-Path "$regPath\$keyName") {
    Remove-ItemProperty -Path $regPath -Name $keyName -ErrorAction SilentlyContinue
    Write-Host "DirectX game optimization removed."
}
else {
    Write-Host "Registry key does not exist, nothing to remove."
}

scripts.apply:
$appsList = 'Microsoft.GamingApp', 'Microsoft.XboxGameOverlay', 'Microsoft.XboxGamingOverlay'

foreach ($app in $appsList) {
    Get-AppxPackage -Name $app -AllUsers | Remove-AppxPackage
    Get-AppxProvisionedPackage -Online | Where-Object DisplayName -EQ $app | Remove-AppxProvisionedPackage -Online
}

if (Test-Path -Path "$env:ProgramFiles\PowerShell\7") {
    Write-Host "PowerShell 7 is already installed."
} else {
    Write-Host "Installing PowerShell 7..."
    winget install --id Microsoft.PowerShell --silent
}

$targetTerminalName = "PowerShell"

if (-not (Get-Command "wt" -ErrorAction SilentlyContinue)) {
    Write-Host "Windows Terminal not installed. Exiting..."
    exit
}

$settingsPath = "$env:LOCALAPPDATA\Packages\Microsoft.WindowsTerminal_8wekyb3d8bbwe\LocalState\settings.json"
if (-not (Test-Path -Path $settingsPath)) {
    Write-Host "Windows Terminal settings.json not found."
    exit
}

$settingsContent = Get-Content -Path $settingsPath | ConvertFrom-Json
$ps7Profile = $settingsContent.profiles.list | Where-Object { $_.name -eq $targetTerminalName }

if ($ps7Profile) {
    $settingsContent.defaultProfile = $ps7Profile.guid
    $settingsContent | ConvertTo-Json -Depth 100 | Set-Content -Path $settingsPath
    Write-Host "Default profile set to PowerShell 7"
} else {
    Write-Host "No PowerShell 7 profile found."
}

scripts.unapply:
$targetTerminalName = "Windows PowerShell"

if (-not (Get-Command "wt" -ErrorAction SilentlyContinue)) {
    Write-Host "Windows Terminal not installed. Exiting..."
    exit
}

$settingsPath = "$env:LOCALAPPDATA\Packages\Microsoft.WindowsTerminal_8wekyb3d8bbwe\LocalState\settings.json"
if (-not (Test-Path -Path $settingsPath)) {
    Write-Host "Windows Terminal settings.json not found."
    exit
}

$settingsContent = Get-Content -Path $settingsPath | ConvertFrom-Json
$ps5Profile = $settingsContent.profiles.list | Where-Object { $_.name -eq $targetTerminalName }

if ($ps5Profile) {
    $settingsContent.defaultProfile = $ps5Profile.guid
    $settingsContent | ConvertTo-Json -Depth 100 | Set-Content -Path $settingsPath
    Write-Host "Default profile reverted to PowerShell 5"
} else {
    Write-Host "No Windows PowerShell profile found."
}

scripts.apply:
$ultimatePlan = powercfg -l | Select-String "Ultimate Performance"

  if (-not $ultimatePlan) {
      Write-Host "Ultimate Performance plan not found. Creating..." 
      powercfg -duplicatescheme e9a42b02-d5df-448d-aa00-03f14749eb61
  } else {
      Write-Host "Ultimate Performance plan already exists." 
  }

  # Get the GUID of the Ultimate Performance plan
  $ultimatePlanGUID = (powercfg -l | Select-String "Ultimate Performance").ToString().Split()[3]

  # Set it as the active plan
  powercfg -setactive $ultimatePlanGUID

  Write-Host "Ultimate Performance power plan is now active."

scripts.unapply:
$balancedGUID = "381b4222-f694-41f0-9685-ff5bb260df2e"

  # check if the Balanced plan exists
  $balancedExists = powercfg -l | Select-String $balancedGUID

  if ($balancedExists) {
      powercfg -setactive $balancedGUID
      Write-Host "Balanced power plan is now active." 
  } else {
      Write-Host "Balanced power plan not found. Creating a new Balanced plan..." 
      powercfg -duplicatescheme $balancedGUID
      powercfg -setactive $balancedGUID
      Write-Host "Balanced power plan created and activated." 
  }

$balancedGUID = "381b4222-f694-41f0-9685-ff5bb260df2e"
  
